import React, { useState } from 'react';
import { addEvent } from '../service/api';
import {useNavigate} from 'react-router-dom'
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import { Typography, makeStyles } from '@material-ui/core';
import {Link} from 'react-router-dom'

const initialValue = {
    name: '',
    description: '',
    event_date: '',
    location: ''
}

const useStyles = makeStyles({
    container: {
        width: '50%',
        margin: '5% 0 0 25%',
        '& > *': {
            marginTop: 20
        }
    }
})



const AddEvents = () => {
    let navigate = useNavigate();
    const classes = useStyles();
    const [event, setEvents] = useState(initialValue);
    const {name, description, event_date, select} = event;


    const onValueChange = (e) => {
        console.log(e.target.value);
        setEvents({...event, [e.target.name]: e.target.value})
    }

    const addEventDetails = async() => {
        await addEvent(event);
        navigate.push('./all');
    }

    return (
        <div className="container p-5">
            <form className="form-group">
                <h1 style={{justifyContent: 'center', textAlign: 'center', fontWeight: 'bold', fontSize: 30}}>Create New Events</h1>
                <div className="row jumbotron justify-content-center">

                    <div className="col-md-4 mb-3 mt-3">
                        <label  for="name">Name</label>
                        <input type="text" value={name}  name="name" placeholder="Name" className="form-control" onChange={(e) => onValueChange(e.target.value)}/>
                    </div>

                    <div className="col-md-4 mb-3 mt-3">
                        <label for="date">Date</label>
                        <input type="date" value={event_date} name="date" placeholder="2022-02-13" className="form-control" onChange={(e) => onValueChange(e.target.value)}/>
                    </div>

                    <div className="col-sm-8  mb-3 mt-3">
                        <label for="sel1" class="form-label">Select location:</label>
                        <select className="form-select" id="sel2" name="select" placeholder="Select location" >
                            {/* {locations.map(item => {  
                                return (
                                <option key={item.id} value={item.id}>
                                    {item.name}
                                </option>)})} */}
                        </select>
                    </div>

                    <div>
                        <label for="comment">Description</label>
                        <textarea class="form-control" rows="4" id="comment" type="text" name="description" value={description} placeholder="Description" onChange={(e) => onValueChange(e.target.value)} style={{resize: 'none'}} ></textarea> 
                        {/* <label for="description">Description</label>
                        <textarea type="text" rows="5" id="lname" name="description" value={description} placeholder="Description" onChange={(e) => onValueChange(e.target.value)} style={{resize: 'none'}}></textarea> */}
                    </div>

                    <br/>
                    <div className="mb-3 mt-3" style={{textAlign:'center', marginTop: -40}}>
                        <Button onClick={() => addEventDetails()}>Add Events</Button>
                    </div>
                        <Link to="/all" className="col-md-12 " style={{textAlign:'center',}}><Button variant="contained" color="primary">Cancel</Button></Link>
                </div>
            </form>
        </div>

    );
};

export default AddEvents;
