import React, { useState, useEffect } from 'react';
import { Form, Input, TextArea, Button, Select } from 'semantic-ui-react'
import { addEvent, getLocations } from '../service/api';
import {useNavigate} from 'react-router-dom'
import {Link} from 'react-router-dom';
import moment from 'moment'



const initialValue = {
    name: '',
    description: '',
    event_date: '',
    location: ''
    }





const AddEvents = () => {
    let navigate = useNavigate();
    const [event, setEvents] = useState(initialValue);
    const [locate, setLocate] = useState([]);
    const [select, setSelect] = useState("");




    // const onValueChange = (e) => {
    //     console.log('value:::::::', e.target.value);
    //     setEvents({...event, [e.target.name]: e.target.value})
    // }

    // const onValueEvent = (e) => {
    //     console.log(e.target.value);
    //     setLocate({...locate, [e.target.locate]: e.target.value})
    // }

    // const handleLogin = () => {
    //     console.log(name, description, event_date);
    //   };

    // const addEventDetails = async() => {
    //     await addEvent(event);
    //     navigate.push('./all');
    // }



    useEffect(() => {
        getAllLocations();
    }, []);

    const getAllLocations = async () => {
        let response = await getLocations();
        setLocate(response.data);
    }


    return (
        <Form>
            <Form.Group widths='equal'>
            <Form.Field
                id='form-input-control-first-name'
                control={Input}
                label='First name'
                placeholder='First name'
            />
            <Form.Field
                id='form-input-control-last-name'
                control={Input}
                label='Last name'
                placeholder='Last name'
            />
            <Form.Field
                control={Select}
                options={setSelect}
                label={{ children: 'Location', htmlFor: 'form-select-control-location' }}
                placeholder='Location'
                search
                searchInput={{ id: 'form-select-control-location' }}
            />
            </Form.Group>
            <Form.Field
            id='form-textarea-control-opinion'
            control={TextArea}
            label='Opinion'
            placeholder='Opinion'
            />
            <Form.Field
            id='form-input-control-error-email'
            control={Input}
            label='Email'
            placeholder='joe@schmoe.com'
            error={{
                content: 'Please enter a valid email address',
                pointing: 'below',
            }}
            />
            <Form.Field
            id='form-button-control-public'
            control={Button}
            content='Confirm'
            label='Label with htmlFor'
            />
        </Form>

    );
};

export default AddEvents;
